const { app, BrowserWindow, ipcMain, screen, Tray, Menu, nativeImage } = require('electron');
const path = require('path');

let mainWindow;
let settingsWindow;
let tray;
let isVisible = true;

function createWindow() {
  const { width, height } = screen.getPrimaryDisplay().workAreaSize;
  
  mainWindow = new BrowserWindow({
    width: width, // Full screen width
    height: height, // Full screen height
    x: 0,
    y: 0,
    frame: false,
    alwaysOnTop: true,
    transparent: true,
    resizable: false,
    minimizable: false,
    maximizable: false,
    skipTaskbar: true, // Don't show in taskbar
    show: true, // Show window on startup
    icon: path.join(__dirname, '../assets/icon.ico'),
    webPreferences: {
      nodeIntegration: false,
      contextIsolation: true,
      preload: path.join(__dirname, 'preload.js'),
      webSecurity: false // Needed for speech recognition
    }
  });

  mainWindow.loadFile(path.join(__dirname, 'chat-new.html'));

  // Show the window after loading
  mainWindow.once('ready-to-show', () => {
    mainWindow.show();
    isVisible = true;
    // Start in click-through mode except for the trigger area
    mainWindow.setIgnoreMouseEvents(true, { forward: true });
  });

  // Remove menu bar
  mainWindow.setMenuBarVisibility(false);

  // Handle window blur (clicking outside)
  mainWindow.on('blur', () => {
    // Send message to renderer to minimize chat if expanded
    if (mainWindow && !mainWindow.isDestroyed()) {
      mainWindow.webContents.send('window-blur');
    }
  });

  // Make window draggable
  mainWindow.webContents.on('did-finish-load', () => {
    mainWindow.webContents.insertCSS(`
      body {
        -webkit-app-region: drag;
        user-select: none;
      }
      input, textarea, button, .interactive {
        -webkit-app-region: no-drag;
      }
    `);
  });

  // Handle window closed
  mainWindow.on('closed', () => {
    mainWindow = null;
  });

  // Prevent window from being closed, hide instead
  mainWindow.on('close', (event) => {
    if (app.quitting !== true) {
      event.preventDefault();
      hideWindow();
    }
  });
}

function createSettingsWindow() {
  if (settingsWindow) {
    settingsWindow.focus();
    return;
  }

  const { width, height } = screen.getPrimaryDisplay().workAreaSize;
  
  settingsWindow = new BrowserWindow({
    width: 500,
    height: 800,
    x: Math.round((width - 500) / 2),
    y: Math.round((height - 800) / 2),
    frame: false,
    alwaysOnTop: true,
    transparent: true,
    resizable: false,
    minimizable: false,
    maximizable: false,
    skipTaskbar: true,
    parent: mainWindow,
    modal: false,
    icon: path.join(__dirname, '../assets/icon.ico'),
    webPreferences: {
      nodeIntegration: false,
      contextIsolation: true,
      preload: path.join(__dirname, 'preload.js'),
      webSecurity: false
    }
  });

  settingsWindow.loadFile(path.join(__dirname, 'settings.html'));

  settingsWindow.setMenuBarVisibility(false);

  settingsWindow.on('closed', () => {
    settingsWindow = null;
  });

  // Make settings window draggable
  settingsWindow.webContents.on('did-finish-load', () => {
    settingsWindow.webContents.insertCSS(`
      body {
        -webkit-app-region: drag;
        user-select: none;
      }
      input, textarea, button, select, .interactive {
        -webkit-app-region: no-drag;
      }
    `);
  });
}

function showWindow() {
  if (mainWindow) {
    mainWindow.show();
    mainWindow.focus();
    isVisible = true;
  }
}

function hideWindow() {
  if (mainWindow) {
    mainWindow.hide();
    isVisible = false;
  }
}

function toggleWindow() {
  if (isVisible) {
    hideWindow();
  } else {
    showWindow();
  }
}

function createTray() {
  // Create a simple icon for the tray (16x16 pixels)
  const iconData = nativeImage.createFromDataURL(`data:image/png;base64,`);
  
  tray = new Tray(iconData);
  
  const contextMenu = Menu.buildFromTemplate([
    {
      label: 'Show Zettel',
      click: () => {
        showWindow();
      }
    },
    {
      label: 'Hide Zettel',
      click: () => {
        hideWindow();
      }
    },
    { type: 'separator' },
    {
      label: 'Settings',
      click: () => {
        createSettingsWindow();
      }
    },
    { type: 'separator' },
    {
      label: 'Quit Zettel',
      click: () => {
        app.quitting = true;
        app.quit();
      }
    }
  ]);

  tray.setToolTip('Zettel - AI Assistant');
  tray.setContextMenu(contextMenu);

  // Handle tray click to toggle window
  tray.on('click', () => {
    toggleWindow();
  });

  // Handle double-click to show window
  tray.on('double-click', () => {
    showWindow();
  });
}

app.whenReady().then(() => {
  createWindow();
  createTray();

  app.on('activate', () => {
    if (BrowserWindow.getAllWindows().length === 0) {
      createWindow();
    } else {
      showWindow();
    }
  });
});

app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});

app.on('before-quit', () => {
  app.quitting = true;
});

// IPC handlers
ipcMain.handle('toggle-visibility', () => {
  toggleWindow();
});

ipcMain.handle('minimize-window', () => {
  hideWindow();
});

ipcMain.handle('quit-app', () => {
  app.quitting = true;
  app.quit();
});

ipcMain.handle('show-settings', () => {
  createSettingsWindow();
});

ipcMain.handle('show-main-window', () => {
  if (settingsWindow) {
    settingsWindow.close();
  }
  showWindow();
});

ipcMain.handle('set-click-through', (event, clickThrough) => {
  if (mainWindow && !mainWindow.isDestroyed()) {
    if (clickThrough) {
      // Make window click-through except for trigger area
      mainWindow.setIgnoreMouseEvents(true, { forward: true });
    } else {
      // Make window interactive
      mainWindow.setIgnoreMouseEvents(false);
    }
  }
});

ipcMain.handle('update-settings', (event, settings) => {
  // Broadcast settings update to main window
  if (mainWindow) {
    mainWindow.webContents.send('settings-updated', settings);
  }
});

// Navigation handlers
ipcMain.handle('navigate-to-chat', () => {
  if (mainWindow && !mainWindow.isDestroyed()) {
    mainWindow.loadFile(path.join(__dirname, 'chat-new.html'));
  }
});

ipcMain.handle('navigate-to-notes-list', () => {
  if (mainWindow && !mainWindow.isDestroyed()) {
    mainWindow.loadFile(path.join(__dirname, 'notes-list.html'));
  }
});

ipcMain.handle('navigate-to-note-editor', (event, noteId) => {
  if (mainWindow && !mainWindow.isDestroyed()) {
    const url = path.join(__dirname, 'note-editor.html') + (noteId ? `?id=${noteId}` : '');
    mainWindow.loadURL('file://' + url);
  }
});
